angular.module('app')

.factory('photos', ['$rootScope', '$http', '$mdDialog', 'folders', 'utils', function($rootScope, $http, $mdDialog, folders, utils) {
    var photos = {

        //all photos user has deleted/moved to trash
        trashed: {
            dirty: true,
            items: []
        },

        //all photos user has added to favorites
        favorites: {
            dirty: true,
            items: []
        },

        /**
         * Fetch a photo with given id from server.
         *
         * @param id
         * @returns promise
         */
        get: function(id) {
            return $http.get($rootScope.baseUrl+'photos/'+id);
        },

        /**
         * Fetch all photos from the server.
         *
         * @param {boolean} all
         * @returns promise
         */
        getAll: function(all) {
            return $http.get($rootScope.baseUrl+'photos?all='+all);
        },

        /**
         * Fetch all photos user has moved to trash from the server.
         *
         * @returns promise
         */
        getTrashed: function() {
            return $http.get($rootScope.baseUrl+'user-trash').success(function(data) {
                photos.trashed.items = data;
                photos.trashed.dirty = false;
            });
        },

        /**
         * Fetch all photos user has marked as favorites.
         *
         * @returns promise
         */
        getFavorites: function() {
            return $http.get($rootScope.baseUrl+'labels/favorite').success(function(data) {
                photos.favorites.items = data;
                photos.favorites.dirty = false;
            });
        },

        /**
         * Attach photos with given ids (attach_id) to currently logged in user.
         *
         * @param {array} ids
         * @returns {promise}
         */
        attachToUser: function(ids) {
            return $http.post($rootScope.baseUrl + 'photo/attach-to-user', {ids: ids}).success(function(data) {
                if (data) {
                    utils.showToast(data);
                }
            });
        },

        save: function(photo) {
            return $http.post($rootScope.baseUrl+'photos', photo).success(function(data) {
                folders.selected.photos.push(data);
                $rootScope.$emit('activity.happened', 'created', 'photo', data);
            });
        },

        /**
         * Restore given photo from trash to it's original folder.
         *
         * @param {object} photo
         */
        restore: function(photo) {
            $http.post($rootScope.baseUrl+'trash/restore/'+photo.id).success(function(data) {
                for (var i = 0; i < photos.trashed.items.length; i++) {
                    if (photos.trashed.items[i].id == data.id) {
                        var p = photos.trashed.items[i],
                            folder = folders.getById(p.folder_id);

                        if (folder && angular.isArray(folder.photos)) {
                            folder.photos.push(p);
                        }

                        photos.trashed.items.splice(i, 1);
                        photos.selected = false;
                        utils.showToast('photoRestoreSuccess', true);
                        break;
                    }
                }
            })
        },

        rename: function(id) {
            $http.put($rootScope.baseUrl + 'photos/' + id, { name: this.photoNameModel }).success(function(data) {
                var folder = folders.getById(data.folder_id);

                if ( ! folder || ! folder.photos) {
                    photos.closeModal();
                    $rootScope.$emit('activity.happened', 'renamed', 'photo', data);
                    return utils.showToast('photoRenameSuccess', true);
                } else {
                    for (var i = 0; i < folder.photos.length; i++) {
                        if (folder.photos[i].id == data.id) {
                            folder.photos[i] = data;
                            photos.closeModal();
                            $rootScope.$emit('activity.happened', 'renamed', 'photo', data);
                            return utils.showToast('photoRenameSuccess', true);
                        }
                    }
                }
            })
        },

        /**
         * Move given photo to given folder.
         *
         * @param {object} photo
         * @param {int}   folderId
         */
        moveToFolder: function(photo, folderId) {
            $http.put($rootScope.baseUrl + 'photos/' + photo.id, { folder_id: folderId }).success(function(data) {
                for (var i = 0; i < folders.selected.photos.length; i++) {
                    if (folders.selected.photos[i].id == data.id) {

                        //delete photo from older folder
                        folders.selected.photos.splice(i, 1);
                    }
                }

                //move photo to new folder
                for (var j = 0; j < folders.available.length; j++) {
                    if (folders.available[j].id == folderId) {
                        if (angular.isArray(folders.available[j].photos)) {
                            folders.available[j].photos.push(data);
                        } else {
                            folders.available[j].photos = [data];
                        }

                        photos.selected = false;
                        photos.closeModal();
                        $rootScope.$emit('activity.happened', 'moved', 'photo', data);
                        return utils.showToast('photoMoveSuccess', true);
                    }
                }
            })
        },

        /**
         * Open modal for renaming selected photo.
         *
         * @param {string} name
         */
        openRenameModal: function(name) {
            this.photoNameModel = name;

            $mdDialog.show({
                templateUrl: 'assets/views/modals/renamePhoto.html',
                controller: ['$scope', 'photos', 'selectedItem', function($scope, photos, selectedItem) {
                    $scope.photos = photos;
                    $scope.selectedItem = selectedItem;
                }],
                onComplete: function() {
                    $('#photo-name').focus();
                }
            });
        },

        /**
         * Select a photo by given id.
         *
         * @param {int|string} id
         * @returns {*}
         */
        selectById: function(id) {
            for (var i = 0; i < folders.selected.photos.length; i++) {
                if (folders.selected.photos[i].id == id) {
                    this.selected = folders.selected.photos[i];
                }
            }
        },

        closeModal: function() {
            $mdDialog.hide();
            this.photoNameModel = false;
        },
    };

    return photos;
}]);
angular.module('app').factory('selectedItem', ['$rootScope', '$mdDialog', '$http', '$timeout', 'utils', 'photos', 'folders', 'gallery', 'labels', 'deleter', function($rootScope, $mdDialog, $http, $timeout, utils, photos, folders, gallery, labels, deleter) {
    var selectedItem = {

        /**
         * Currently selected item in dashboard (folder or photo)
         */
        item: false,

        /**
         * Get currently selected item or its property.
         *
         * @param {string|undefined} prop
         * @returns {*}
         */
        get: function (prop) {
            if ( ! this.item) {
                this.item = folders.selected;
            }

            if (!prop) return this.item;

            return this.item && this.item[prop];
        },

        /**
         * Set selected item as folder we're currently in
         * so all items and sub-folders are deselected.
         */
        deselect: function() {
            this.item = folders.selected;
        },

        /**
         * Set currently selected item
         *
         * @param {object} item
         * @returns {*}
         */
        set: function (item) {
            return this.item = item;
        },

        /**
         * Open share dialog for folder or file.
         */
        share: function () {
            $mdDialog.show({
                templateUrl: 'assets/views/modals/share.html',
                controller: 'ShareModalController',
                clickOutsideToClose: true,
                locals: {folderName: this.item.name},
                onComplete: function() {
                    var input = $('#share-modal-input')[0];
                    input.setSelectionRange(0, input.value.length);
                }
            });
        },

        /**
         * Restore deleted file or folder from trash.
         */
        restore: function () {
            photos.restore(this.get());
        },

        /**
         * Open currently selected photo in editor.
         */
        openInEditor: function() {
            utils.toState('editor', { id: this.get('id') });
        },

        /**
         * Delete selected photo or album.
         */
        delete: function () {
            deleter.delete(this.get());
        },

        /**
         * Update selected item.
         *
         * @param {object|undefined} payload
         * @return promise
         */
        update: function(payload) {
            var slug = this.get('type') === 'photo' ? 'photos' : 'folders';

            return $http.put($rootScope.baseUrl+slug+'/'+this.get('id'), payload || this.get()).success(function(data) {
                if (selectedItem.get('type') === 'folder') {
                    folders.selected = data;
                } else {
                    selectedItem.set(data);

                    if (angular.isArray(folders.selected.photos)) {
                        for (var i = 0; i < folders.selected.photos.length; i++) {
                            if (folders.selected.photos[i].id == data.id) {
                                folders.selected.photos[i] = data; break;
                            }
                        }
                    }
                }
            });
        },

        /**
         * Open links modal for currently selected file or folder.
         */
        getLinks: function () {
            $mdDialog.show({
                templateUrl: 'assets/views/modals/links.html',
                clickOutsideToClose: true,
                onComplete: function () {
                    $('.link-input').first().focus()[0].select();
                },
                controller: ['$scope', 'selectedItem', 'utils', function ($scope, selectedItem, utils) {
                    $scope.shareable = selectedItem.get();
                    $scope.close = utils.closeModal;
                }]
            });
        },

        /**
         * Copy selected item. Works for photos only currently.
         */
        copy: function() {
            $http.post('photos/'+this.get('id')+'/copy').success(function(data) {
                folders.selected.photos.push(data);
                selectedItem.set(data);
                utils.showToast('PhotoCopySuccess', true);
            }).error(function(data) {
                if(angular.isString(data)) {
                    utils.showToast(data);
                }
            })
        },

        /**
         * Open rename modal for currently open file or folder.
         */
        rename: function () {
            if (this.get('type') === 'folder') {
                folders.openRenameModal(this.get().name);
            } else {
                photos.openRenameModal(this.get().name);
            }
        },

        /**
         * Open modal to move photo to a different folder.
         */
        move: function () {
            $mdDialog.show({
                templateUrl: 'assets/views/modals/move.html',
                controller: ['$scope', 'photos', 'folders', 'selectedItem', function($scope, photos, folders, selectedItem) {
                    $scope.photos = photos;
                    $scope.selectedItem = selectedItem;
                    $scope.folders = folders;
                }]
            });
        },

        /**
         * Start a download for selected photo or folder using iframe hack.
         */
        download: function () {
            var self = this;

            $('#download-iframe').remove();
            $('<iframe id="download-iframe" style="display: none"></iframe>')
                .appendTo('body')
                .attr('src', $rootScope.baseUrl + self.get('type') + '/' + self.get().share_id + '/download')
        },

        /**
         * add currently selected photo to favorites.
         */
        favorite: function () {
            this.addLabel('favorite');
        },

        /**
         * Add given label to currently selected photo.
         *
         * @param {string} name
         */
        addLabel: function(name) {
            labels.addLabel(name, this.get());
        },

        /**
         * Remove given label from currently selected photo.
         *
         * @param {string} name
         */
        removeLabel: function(name) {
            labels.removeLabel(name, this.get());
        },

        /**
         * Open preview gallery for selected item.
         */
        preview: function () {
            var self = this;

            $timeout(function () {
                if (self.get('type') === 'folder') {
                    return folders.preview(self.get('name'));
                }

                gallery.open(self.get());
            });
        }
    };

    /**
     * Deselect any selected files on state change.
     */
    $rootScope.$on('$stateChangeSuccess', function() {
        selectedItem.deselect();
    });

    $rootScope.$on('activity.happened', function(e, action, type, items) {
        if ( ! angular.isArray(items)) items = [items];

        for (var i = 0; i < items.length; i++) {
            if (items[i].id === selectedItem.get('id')) {
                if (action === 'deleted' || action === 'moved') {

                    if (type === 'folder') {
                        folders.open('root');
                    }

                    selectedItem.deselect(); break;
                } else {
                    selectedItem.set(items[i]);
                }
            }
        }
    });

    return selectedItem;
}]);

